src/pages/FindMechanic.tsx.content:
import { useState, useMemo, useEffect } from 'react';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Search } from 'lucide-react';
import MechanicCard from '@/components/MechanicCard';
import { getMechanics, GetMechanicsOutputType } from 'zite-endpoints-sdk';

type Mechanic = GetMechanicsOutputType['mechanics'][0];

export default function FindMechanic() {
  const [search, setSearch] = useState('');
  const [sortBy, setSortBy] = useState('name-asc');
  const [mechanics, setMechanics] = useState<Mechanic[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    document.title = 'Find a Mechanic | DriveON Pro Solutions';
    loadMechanics();
  }, []);

  const loadMechanics = async () => {
    try {
      setLoading(true);
      const result = await getMechanics({});
      setMechanics(result.mechanics);
    } catch (error) {
      console.error('Failed to load mechanics:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredAndSorted = useMemo(() => {
    let result = [...mechanics];

    if (search) {
      const query = search.toLowerCase();
      result = result.filter(
        (m) =>
          m.name.toLowerCase().includes(query) ||
          m.area.toLowerCase().includes(query)
      );
    }

    result.sort((a, b) => {
      if (sortBy === 'name-asc') {
        return a.name.localeCompare(b.name);
      } else if (sortBy === 'area-asc') {
        return a.area.localeCompare(b.area);
      }
      return 0;
    });

    return result;
  }, [mechanics, search, sortBy]);

  return (
    <>
      <div className="min-h-screen py-12">
        <div className="container mx-auto px-4">
          <div className="max-w-5xl mx-auto space-y-8">
            <div className="text-center space-y-2">
              <h1 className="text-3xl md:text-4xl font-bold text-foreground">
                Find a Mechanic
              </h1>
              <p className="text-muted-foreground">
                Tap to call or open their city page.
              </p>
            </div>

            <div className="flex flex-col sm:flex-row gap-4">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                <Input
                  placeholder="Search by name or area..."
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  className="pl-10"
                />
              </div>
              <Select value={sortBy} onValueChange={setSortBy}>
                <SelectTrigger className="w-full sm:w-48">
                  <SelectValue placeholder="Sort by" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="name-asc">Name A–Z</SelectItem>
                  <SelectItem value="area-asc">Area A–Z</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {loading ? (
              <div className="text-center py-12">
                <p className="text-muted-foreground">Loading mechanics...</p>
              </div>
            ) : filteredAndSorted.length === 0 ? (
              <div className="text-center py-12">
                <p className="text-muted-foreground">No mechanics found.</p>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredAndSorted.map((mechanic) => (
                  <MechanicCard
                    key={mechanic.id}
                    name={mechanic.name}
                    area={mechanic.area}
                    phone={mechanic.phone}
                    subdomain={mechanic.subdomain}
                    page_url={mechanic.pageUrl}
                  />
                ))}
              </div>
            )}

            <div className="bg-card rounded-2xl border border-border p-8 text-center">
              <p className="text-muted-foreground">
                Want to be listed?{' '}
                <a
                  href="mailto:hello@driveonpro.com"
                  className="text-primary font-medium hover:underline"
                >
                  Email hello@driveonpro.com
                </a>
              </p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

src/pages/MechanicPage.tsx.content:
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import MechanicHero from '@/components/MechanicHero';
import AddMechanicToHomeScreen from '@/components/AddMechanicToHomeScreen';
import { getMechanics, GetMechanicsOutputType } from 'zite-endpoints-sdk';

type Mechanic = GetMechanicsOutputType['mechanics'][0];

export default function MechanicPage() {
  const { subdomain } = useParams();
  const [mechanic, setMechanic] = useState<Mechanic | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadMechanic();
  }, [subdomain]);

  const loadMechanic = async () => {
    try {
      setLoading(true);
      const result = await getMechanics({});
      const found = result.mechanics.find(m => m.subdomain === subdomain);

      if (found) {
        setMechanic(found);
        document.title = `${found.name} | DriveON Pro`;
      }
    } catch (error) {
      console.error('Failed to load mechanic:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-muted-foreground">Loading...</p>
      </div>
    );
  }

  if (!mechanic) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center space-y-4">
          <h1 className="text-3xl font-bold text-foreground">Mechanic Not Found</h1>
          <p className="text-muted-foreground">This mechanic page does not exist.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      <MechanicHero
        name={mechanic.name}
        area={mechanic.area}
        phone={mechanic.phone}
        subdomain={mechanic.subdomain}
        pageUrl={mechanic.pageUrl}
      />
      <AddMechanicToHomeScreen mechanicName={mechanic.name} />
    </div>
  );
}

src/pages/RequestQuote.tsx.content:
import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Card } from '@/components/ui/card';
import { toast } from 'sonner';
import { getMechanics, GetMechanicsOutputType } from 'zite-endpoints-sdk';

type Mechanic = GetMechanicsOutputType['mechanics'][0];

export default function RequestQuote() {
  const [mechanics, setMechanics] = useState<Mechanic[]>([]);
  const [loading, setLoading] = useState(false);

  // Form fields
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [location, setLocation] = useState('');
  const [selectedMechanic, setSelectedMechanic] = useState('');
  const [vehicleMake, setVehicleMake] = useState('');
  const [vehicleModel, setVehicleModel] = useState('');
  const [vehicleYear, setVehicleYear] = useState('');
  const [serviceType, setServiceType] = useState('');
  const [description, setDescription] = useState('');
  const [urgency, setUrgency] = useState('');

  useEffect(() => {
    document.title = 'Request a Quote | DriveON Pro Solutions';
    loadMechanics();
  }, []);

  const loadMechanics = async () => {
    try {
      const result = await getMechanics({});
      setMechanics(result.mechanics);
    } catch (error) {
      console.error('Failed to load mechanics:', error);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!name || !phone || !location || !selectedMechanic || !serviceType || !description) {
      toast.error('Please fill in all required fields');
      return;
    }

    setLoading(true);
    // Simulate submission
    await new Promise(resolve => setTimeout(resolve, 1500));
    toast.success('Quote request submitted! We\'ll contact you shortly.');

    // Reset form
    setName('');
    setPhone('');
    setEmail('');
    setLocation('');
    setSelectedMechanic('');
    setVehicleMake('');
    setVehicleModel('');
    setVehicleYear('');
    setServiceType('');
    setDescription('');
    setUrgency('');
    setLoading(false);
  };

  return (
    <div className="min-h-screen py-12">
      <div className="container mx-auto px-4">
        <div className="max-w-3xl mx-auto space-y-8">
          <div className="text-center space-y-2">
            <h1 className="text-3xl md:text-4xl font-bold text-foreground">
              Request a Quote
            </h1>
            <p className="text-muted-foreground">
              Fill out the form below and we'll connect you with the right mechanic
            </p>
          </div>

          <Card className="p-6 md:p-8">
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="space-y-4">
                <h2 className="text-xl font-semibold text-foreground">Your Information</h2>

                <div className="grid md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="name">Full Name *</Label>
                    <Input
                      id="name"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      placeholder="John Doe"
                      required
                    />
                  </div>
                  <div>
                    <Label htmlFor="phone">Phone Number *</Label>
                    <Input
                      id="phone"
                      type="tel"
                      value={phone}
                      onChange={(e) => setPhone(e.target.value)}
                      placeholder="(405) 555-0100"
                      required
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="john@example.com"
                  />
                </div>

                <div>
                  <Label htmlFor="location">Your Location *</Label>
                  <Input
                    id="location"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    placeholder="City or ZIP code"
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="mechanic">Preferred Mechanic *</Label>
                  <Select value={selectedMechanic} onValueChange={setSelectedMechanic}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select a mechanic" />
                    </SelectTrigger>
                    <SelectContent>
                      {mechanics.map((mechanic) => (
                        <SelectItem key={mechanic.id} value={mechanic.id}>
                          {mechanic.name} - {mechanic.area}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="space-y-4">
                <h2 className="text-xl font-semibold text-foreground">Vehicle Details</h2>

                <div className="grid md:grid-cols-3 gap-4">
                  <div>
                    <Label htmlFor="year">Year</Label>
                    <Input
                      id="year"
                      value={vehicleYear}
                      onChange={(e) => setVehicleYear(e.target.value)}
