                    />
                  </div>
                  <div>
                    <Label htmlFor="make">Make</Label>
                    <Input
                      id="make"
                      value={vehicleMake}
                      onChange={(e) => setVehicleMake(e.target.value)}
                      placeholder="Toyota"
                    />
                  </div>
                  <div>
                    <Label htmlFor="model">Model</Label>
                    <Input
                      id="model"
                      value={vehicleModel}
                      onChange={(e) => setVehicleModel(e.target.value)}
                      placeholder="Camry"
                    />
                  </div>
                </div>
              </div>

              <div className="space-y-4">
                <h2 className="text-xl font-semibold text-foreground">Service Details</h2>

                <div>
                  <Label htmlFor="serviceType">Type of Service *</Label>
                  <Select value={serviceType} onValueChange={setServiceType}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select service type" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="diagnostics">Diagnostics</SelectItem>
                      <SelectItem value="brakes">Brakes</SelectItem>
                      <SelectItem value="battery">Battery Replacement</SelectItem>
                      <SelectItem value="starter">Starter/Alternator</SelectItem>
                      <SelectItem value="oil">Oil Change & Fluids</SelectItem>
                      <SelectItem value="engine">Engine Repair</SelectItem>
                      <SelectItem value="transmission">Transmission</SelectItem>
                      <SelectItem value="other">Other</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="urgency">How soon do you need service?</Label>
                  <Select value={urgency} onValueChange={setUrgency}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select urgency" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="emergency">Emergency (ASAP)</SelectItem>
                      <SelectItem value="today">Today</SelectItem>
                      <SelectItem value="week">Within a week</SelectItem>
                      <SelectItem value="flexible">Flexible</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="description">Describe the Issue *</Label>
                  <Textarea
                    id="description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="Please describe what's happening with your vehicle..."
                    rows={5}
                    required
                  />
                </div>
              </div>

              <Button
                type="submit"
                className="w-full bg-gradient-to-r from-primary to-accent"
                size="lg"
                disabled={loading}
              >
                {loading ? 'Submitting...' : 'Submit Quote Request'}
              </Button>
            </form>
          </Card>
        </div>
      </div>
    </div>
  );
}

src/components/QuoteForm.tsx.content:
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Card } from '@/components/ui/card';
import { useState } from 'react';
import { toast } from 'sonner';

export default function QuoteForm() {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!name || !phone || !message) {
      toast.error('Please fill in all required fields');
      return;
    }

    setLoading(true);
    // Simulate form submission
    await new Promise(resolve => setTimeout(resolve, 1000));
    toast.success('Quote request sent! We\'ll contact you soon.');
    setName('');
    setPhone('');
    setEmail('');
    setMessage('');
    setLoading(false);
  };

  return (
    <Card className="p-6">
      <h2 className="text-2xl font-bold text-foreground mb-4">Request a Quote</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <Label htmlFor="name">Name *</Label>
          <Input
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Your name"
            required
          />
        </div>
        <div>
          <Label htmlFor="phone">Phone *</Label>
          <Input
            id="phone"
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            placeholder="(405) 555-0100"
            required
          />
        </div>
        <div>
          <Label htmlFor="email">Email</Label>
          <Input
            id="email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="your@email.com"
          />
        </div>
        <div>
          <Label htmlFor="message">What do you need? *</Label>
          <Textarea
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Describe your vehicle issue..."
            rows={4}
            required
          />
        </div>
        <Button
          type="submit"
          className="w-full bg-gradient-to-r from-primary to-accent"
          disabled={loading}
        >
          {loading ? 'Sending...' : 'Send Quote Request'}
        </Button>
      </form>
    </Card>
  );
}

