src/components/MechanicCard.tsx.content:
import { Button } from '@/components/ui/button';
import { Phone, ExternalLink } from 'lucide-react';
import { Link } from 'react-router-dom';

interface MechanicCardProps {
  name: string;
  area: string;
  phone: string;
  subdomain?: string;
  page_url?: string;
}

const sanitizePhone = (phone: string): string => {
  return phone.replace(/[^\d+]/g, '');
};

const buildPageUrl = (subdomain?: string, page_url?: string): string | null => {
  if (page_url) return page_url;
  if (subdomain) return `/mechanic/${subdomain}`;
  return null;
};

export default function MechanicCard({ name, area, phone, subdomain, page_url }: MechanicCardProps) {
  const pageUrl = buildPageUrl(subdomain, page_url);
  const telLink = `tel:${sanitizePhone(phone)}`;

  return (
    <div className="bg-card rounded-2xl border border-border p-6 shadow-sm hover:shadow-md transition-shadow">
      <div className="space-y-4">
        <div>
          <h3 className="text-lg font-bold text-foreground">{name}</h3>
          <p className="text-sm text-muted-foreground mt-1">{area}</p>
        </div>
        <div className="flex flex-col sm:flex-row gap-3">
          <Button asChild className="flex-1 bg-gradient-to-r from-primary to-accent">
            <a href={telLink}>
              <Phone className="w-4 h-4 mr-2" />
              Call
            </a>
          </Button>
          {pageUrl && (
            <Button asChild variant="outline" className="flex-1">
              {page_url ? (
                <a href={pageUrl} target="_blank" rel="noopener noreferrer">
                  <ExternalLink className="w-4 h-4 mr-2" />
                  Open Page
                </a>
              ) : (
                <Link to={pageUrl}>
                  <ExternalLink className="w-4 h-4 mr-2" />
                  Open Page
                </Link>
              )}
            </Button>
          )}
        </div>
      </div>
    </div>
  );
}

