import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card } from '@/components/ui/card';
import { Trash2, Plus, LogOut } from 'lucide-react';
import { toast } from 'sonner';
import { getMechanics, createMechanic, deleteMechanic, GetMechanicsOutputType } from 'zite-endpoints-sdk';

type Mechanic = GetMechanicsOutputType['mechanics'][0];

export default function Admin() {
  const navigate = useNavigate();
  const [mechanics, setMechanics] = useState<Mechanic[]>([]);
  const [loading, setLoading] = useState(true);
  const [name, setName] = useState('');
  const [area, setArea] = useState('');
  const [phone, setPhone] = useState('');
  const [subdomain, setSubdomain] = useState('');
  const [pageUrl, setPageUrl] = useState('');

  useEffect(() => {
    document.title = 'Admin | DriveON Pro Solutions';
    if (localStorage.getItem('adminAuth') !== 'true') {
      navigate('/login');
      return;
    }
    loadMechanics();
  }, [navigate]);

  const loadMechanics = async () => {
    try {
      setLoading(true);
      const result = await getMechanics({});
      setMechanics(result.mechanics);
    } catch (error) {
      console.error('Failed to load mechanics:', error);
      toast.error('Failed to load mechanics');
    } finally {
      setLoading(false);
    }
  };

  const handleAdd = async () => {
    if (!name || !area || !phone) {
      toast.error('Please fill in name, area, and phone');
      return;
    }

    try {
      await createMechanic({
        name,
        area,
        phone,
        subdomain: subdomain || undefined,
        pageUrl: pageUrl || undefined,
      });

      toast.success('Mechanic added successfully');
      setName('');
      setArea('');
      setPhone('');
      setSubdomain('');
      setPageUrl('');
      await loadMechanics();
    } catch (error) {
      console.error('Failed to add mechanic:', error);
      toast.error('Failed to add mechanic');
    }
  };

  const handleDelete = async (id: string) => {
    try {
      await deleteMechanic({ id });
      toast.success('Mechanic deleted');
      await loadMechanics();
    } catch (error) {
      console.error('Failed to delete mechanic:', error);
      toast.error('Failed to delete mechanic');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('adminAuth');
    toast.success('Logged out');
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="min-h-screen py-12 flex items-center justify-center">
        <p className="text-muted-foreground">Loading...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen py-12">
      <div className="container mx-auto px-4 max-w-4xl space-y-8">
        <div className="flex justify-between items-center">
          <h1 className="text-3xl font-bold text-foreground">Admin Panel</h1>
          <Button variant="outline" onClick={handleLogout}>
            <LogOut className="w-4 h-4 mr-2" />
            Logout
          </Button>
        </div>

        <Card className="p-6 space-y-4">
          <h2 className="text-xl font-semibold">Add New Mechanic</h2>
          <div className="grid gap-4">
            <div>
              <Label htmlFor="name">Name *</Label>
              <Input id="name" value={name} onChange={(e) => setName(e.target.value)} />
            </div>
            <div>
              <Label htmlFor="area">Area *</Label>
              <Input id="area" value={area} onChange={(e) => setArea(e.target.value)} />
            </div>
            <div>
              <Label htmlFor="phone">Phone *</Label>
              <Input id="phone" value={phone} onChange={(e) => setPhone(e.target.value)} />
            </div>
            <div>
              <Label htmlFor="subdomain">Subdomain</Label>
              <Input id="subdomain" value={subdomain} onChange={(e) => setSubdomain(e.target.value)} />
            </div>
            <div>
              <Label htmlFor="pageUrl">Page URL</Label>
              <Input id="pageUrl" value={pageUrl} onChange={(e) => setPageUrl(e.target.value)} />
            </div>
            <Button onClick={handleAdd} className="bg-gradient-to-r from-primary to-accent">
              <Plus className="w-4 h-4 mr-2" />
              Add Mechanic
            </Button>
          </div>
        </Card>

        <div className="space-y-4">
          <h2 className="text-xl font-semibold">Current Listings ({mechanics.length})</h2>
          {mechanics.map((mechanic) => (
            <Card key={mechanic.id} className="p-4 flex justify-between items-start">
              <div>
                <h3 className="font-bold">{mechanic.name}</h3>
                <p className="text-sm text-muted-foreground">{mechanic.area}</p>
                <p className="text-sm">{mechanic.phone}</p>
              </div>
              <Button variant="destructive" size="icon" onClick={() => handleDelete(mechanic.id)}>
                <Trash2 className="w-4 h-4" />
              </Button>
            </Card>
          ))}
        </div>
      </div>
    </div>
  );
}

import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from '@/components/ui/button';
